import React, {useState, useEffect, Component} from 'react';
import { connect } from "react-redux";
import Creator from '../Creator/Creator'
import {TasksDiv} from './styled'
import Tasks from '../Tasks/Tasks'
import {getTasks} from '../../actions/Actions'

class Planner extends React.Component {
render(){
    return (
      <div>
        <div><Creator/>
        <button onClick={getTasks}>teste</button></div>
        <TasksDiv>
          <Tasks dia={'Domingo'} testes={tasksList}/>
          <Tasks dia={'Segunda'} testes={this.props.tasksList}/>
          <Tasks dia={'Terça'} />
          <Tasks dia={'Quarta'} />
          <Tasks dia={'Quinta'} />
          <Tasks dia={'Sexta'} />
          <Tasks dia={'Sabado'} />
        </TasksDiv>
      </div>
    )
}}

const mapStateToProps = state => ({tasksList: state.tasks.tasks});

const mapDispatchToProps = (dispatch) => ({
    getTasks: () => dispatch(getTasks())
})

export default connect(mapStateToProps, mapDispatchToProps)(Planner)
