import React, {useState, useEffect, Component} from 'react';
import { connect } from "react-redux";
import Creator from '../Creator/Creator'
import {TasksDiv} from '../../style/styled'
import Tasks from '../Tasks/Tasks'
import {getTasks} from '../../actions/Actions'

class Planner extends React.Component {
  componentDidMount(){
    this.props.getTasks()
  }
render(){
    return (
      <div>
        <div><Creator/></div>
        <TasksDiv>
          <Tasks dia={'Domingo'} tasklist={this.props.tasksList}/>
          <Tasks dia={'Segunda'} tasklist={this.props.tasksList}/>
          <Tasks dia={'Terça'} tasklist={this.props.tasksList}/>
          <Tasks dia={'Quarta'} tasklist={this.props.tasksList}/>
          <Tasks dia={'Quinta'} tasklist={this.props.tasksList}/>
          <Tasks dia={'Sexta'} tasklist={this.props.tasksList}/>
          <Tasks dia={'Sabado'} tasklist={this.props.tasksList}/>
        </TasksDiv>
      </div>
    )
}}

const mapStateToProps = state => ({tasksList: state.tasks.tasks});

const mapDispatchToProps = (dispatch) => ({
  getTasks: () => dispatch(getTasks())
})

export default connect(mapStateToProps, mapDispatchToProps)(Planner)
