import React from 'react';
import TaskItem from '../TaskItem/TaskItem'

export default function Tasks(props) {
    const tasksToDay = props.tasklist
    const tasksFiltered = tasksToDay.filter(produto =>{
        return (produto.day == props.dia)
    })

    return (
        <div>
            <h4>{props.dia}</h4>

            {tasksFiltered.map(element => {
		return (
			<TaskItem
			image={element.image}
			id={element.id}
			name={element.name}
			shipping={element.shipping}
			description={element.description}
			paymentMethod={element.paymentMethod}
			price={element.price}
			mostraPrincipal={this.adicionarProdutoPrincipal}
			adicionaAoCarrinho={this.adicionarProdutoNoCarrinho}
			/>
		)
	})}

        </div>
    );
}
