import React, {useState, useEffect, Component} from 'react';
import { connect } from "react-redux";
import Creator from '../Creator/Creator'
import {TasksDiv} from '../../style/styled'
import Tasks from '../Tasks/Tasks'
import {getTasks} from '../../actions/Actions'

function Planner(props) {
  useEffect(() => {
    props.getTasks()
  }, [])
    return (
      <div>
        <div><Creator/></div>
        <TasksDiv>
          <Tasks dia={'Domingo'} tasklist={props.tasksList} bgColor={'blue'} />
          <Tasks dia={'Segunda'} tasklist={props.tasksList} bgColor={'green'} />
          <Tasks dia={'Terça'} tasklist={props.tasksList} bgColor={'green'} />
          <Tasks dia={'Quarta'} tasklist={props.tasksList} bgColor={'green'} />
          <Tasks dia={'Quinta'} tasklist={props.tasksList} bgColor={'green'} />
          <Tasks dia={'Sexta'} tasklist={props.tasksList} bgColor={'green'} />
          <Tasks dia={'Sabado'} tasklist={props.tasksList} bgColor={'green'} />
        </TasksDiv>
      </div>
    )
}

const mapStateToProps = state => ({tasksList: state.tasks.tasks});

const mapDispatchToProps = (dispatch) => ({
  getTasks: () => dispatch(getTasks())
})

export default connect(mapStateToProps, mapDispatchToProps)(Planner)
