import axios from 'axios'
const baseUrl = 'https://us-central1-missao-newton.cloudfunctions.net/generic/planner-sagan-eduardoteste'

export const createNewTask = async (name, day) => {
    try {
        const body = JSON.stringify({"text":name,"day":day})
        const response = await axios.post(`${baseUrl}`, body);
        console.log(response.data)
    } catch (error) {
    console.log(`erro: ${error}`)
    }

}
