import React, {useState} from 'react';
import {connect} from "react-redux"
import {createNewTask} from '../../actions/actions'

function Creator() {
    const [inputTaskName, setInputTaskName] = useState("");
    const [selectedDay, setSelectedDay] = useState("");

    const handleWhithSubmit = (event) => {
        event.preventDefault()
        createNewTask(inputTaskName, selectedDay)
    }

    return (
        <div>
            <form onSubmit={handleWhithSubmit}>
                <input type='text'
                    onChange={
                        (event) => {
                            setInputTaskName(event.target.value)
                        }
                    }
                    value={inputTaskName}></input>
            <select onChange={
                (event) => {
                    setSelectedDay(event.target.value)
                }
            }>
                <option value="">Selecione...</option>
                <option value="Domingo">Domingo</option>
                <option value="Segunda">Segunda</option>
                <option value="Terça">Terça</option>
                <option value="Quarta">Quarta</option>
                <option value="Quinta">Quinta</option>
                <option value="Sexta">Sexta</option>
                <option value="Sabado">Sabado</option>
            </select>
            <button type='submit'>Cadastrar tarefa</button>
        </form>
    </div>
    );
}

const mapStateToProps = state => ({});

const mapDispatchToProps = (dispatch) => ({
    createNewTask: (name, day) => dispatch(createNewTask(name, day))
})

export default connect(mapStateToProps, mapDispatchToProps)(Creator)
