import React from 'react';
import TaskItem from '../TaskItem/TaskItem'

export default function Tasks(props) {
    const tasksFiltered = props.tasklist.filter(produto =>{(produto.day == props.dia)})

    return (
        <div>
            <h4>{props.dia}</h4>

            {props.tasklist.filter(produto =>{(produto.day == props.dia)}).map(element => {
		return (
			<TaskItem
			id={element.id}
			text={element.text}
			/>
		)
	})}

        </div>
    );
}
