import React, {useState, useEffect} from 'react';
import { connect } from "react-redux";
import Creator from '../Creator/Creator'
import {TasksDiv} from './styled'
import Tasks from '../Tasks/Tasks'
import {getTasks} from '../../actions/Actions'

function Planner(props) {
    const [tasksList2, setTasksList] = useState([{}]);

    useEffect(() => {
      getTasks().then(tasksList => setTasksList(props.tasksList))
    }, [])

    return (
      <div>
        <div><Creator/>
        <button onClick={props.getTasks}>teste</button></div>
        <TasksDiv>
          <Tasks dia={'Domingo'} testes={props.tasksList}/>
          <Tasks dia={'Segunda'} testes={props.tasksList2}/>
          <Tasks dia={'Terça'} />
          <Tasks dia={'Quarta'} />
          <Tasks dia={'Quinta'} />
          <Tasks dia={'Sexta'} />
          <Tasks dia={'Sabado'} />
        </TasksDiv>
      </div>
    )
}

const mapStateToProps = state => ({tasksList: state.tasks.tasks});

const mapDispatchToProps = (dispatch) => ({
    getTasks: () => dispatch(getTasks())
})

export default connect(mapStateToProps, mapDispatchToProps)(Planner)
